var canceled = flow.get("Canceled");

node.status({fill:"blue", shape:"ring", 
            text: "CD_Inserted:" + inserted + " AutoRipCD:" + autoRipCD + " Canceled:" + canceled});

if (inserted && autoRipCD && !canceled) {
    msg.payload = true;
    return msg;
}

if (msg.payload.includes("Unknown Artist")) {
