}

if (!flow.get("Ripping")) return;
if (msg.payload.includes("abcde.")) return;
return msg;

var folder = flow.get("RippingFolder");
var names = folder.split("/");
var filename = names[names.length-1];
var items = filename.split("-");

var artist = items[0];
artist = artist.split('_').join(' ');

if (artist.includes("Unkown")) return;

var album = items[1];
if (items.length > 2) album += items[2];
album = album.split('_').join(' ');

msg.payload = 'cd "' + folder + '";glyrc cover --artist "' + artist + '" --album "' + album + '"';
node.status({fill:"blue", shape:"ring", text: msg.payload});
return msg;

var inserted = flow.get("CD_Inserted");
if (!inserted) {
    msg.payload = "No CD inserted";
    return msg;
}

var wasInserted = flow.get("CD_Inserted");

var inserted = msg.payload.includes("Disc found in drive: audio disc");
flow.set("CD_Inserted", inserted);


if (inserted != wasInserted) {
    if (flow.get("Ripping")) return;

    var msg2 = { payload: "" };
    if (inserted) msg2.payload = "CD inserted";
    else msg2.payload = "CD ejected";

    return [msg, msg2];
}
else {
    return [msg, null];
}

