var output = msg.payload.split("\n");
var internal;
var usb1 = "n.a.";
var usb2 = "n.a.";

for (let i = 0; i < output.length; ++i) {
    var words = output[i].split(/[ ]+/);
    if (words[0].includes("/dev/root")) internal = words[4];
    if (words[0].includes("/dev/sda1")) usb1 =  words[4];
    if (words[0].includes("/dev/sdb1")) usb2 =  words[4];
}
return [  { payload: internal },   { payload: usb1 },  { payload: usb2 }]

var files = msg.payload.split("\n");
files.pop();
var unknown = [];
for (let i = 0; i < files.length; ++i) {
    if (!files[i].startsWith("abcde")) unknown.push(files[i]);
}
msg.payload = ""
msg.options = unknown;
flow.set("options", msg.options);
return msg;

var oldname = flow.get("oldname");
if (oldname === "") return;

var newname = flow.get("newname");
if (newname === "") {
    msg.payload = "No new folder name specified";
    return [null, msg];
}

if (flow.get("options").includes(newname)) {
