var globalByte = global.get("InfraRedEnabledForChannel");
var flowByte = flow.get("InfraRedEnabledForChannel");

if (flowByte != globalByte) {
    global.set("InfraRedEnabledForChannel", flowByte);
    msg.payload = "=IREFC=" + flowByte + "="; // Infra Red EnabledForChannel
}
return msg;

var byte = global.get("InfraRedEnabledForChannel");
node.status({fill:"blue", shape:"ring", text: byte });
var ch1 = byte &   1 ? true : false;
var ch2 = byte &   2 ? true : false;
var ch3 = byte &   4 ? true : false;
var ch4 = byte &   8 ? true : false;
var ch5 = byte &  16 ? true : false;
var ch6 = byte &  32 ? true : false;
var ch7 = byte &  64 ? true : false;
var ch8 = byte & 128 ? true : false;

return [
    {payload: ch1},
    {payload: ch2},
    {payload: ch3},
    {payload: ch4},
    {payload: ch5},
    {payload: ch6},
    {payload: ch7},
    {payload: ch8}
];

var byte = flow.get("InfraRedEnabledForChannel");
if (msg.payload) {
    byte |= msg.topic;
}
else {
    byte = byte & ~msg.topic;
}
flow.set("InfraRedEnabledForChannel", byte);
return msg;

